pattern.forlist:
    @Override
	public View getView(int position, View convertView, ViewGroup parent) {
		View view = null;
		#%localDeclaration%#
		if (convertView == null) {
			#%inflateView%#
			ViewHolder holder=new ViewHolder();
			#%holderFindViewById%#
			view.setTag(holder);
		} else {
			view = convertView;
			ViewHolder holder = (ViewHolder) view.getTag();
		}
		
		return view;
	}

	class ViewHolder {
		#%localDeclaration%#
	}
